import React, { useRef, useEffect } from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import {loadLevel} from './loaders/level.js';
//import Entity from './Entity.js';
import {createMario} from './entities.js';
import Timer from './Timer.js';
//import Compositor from './Compositor.js';
import {setupKeyboard} from './input.js';
import Camera from './Camera.js';
import './canvas.css';
import * as msg from './entities_msg.js';
import Navbar from '../Navbar.js';
import Portfolio from '../Portfolio.js';

const Canvas = (props) => {
  
  const canvasRef = useRef(null);

  useEffect(() => {
    const canvas = canvasRef.current;
    console.log(canvas);
    const context = canvas.getContext('2d');
    context.canvas.width=500;
    context.canvas.height=320;
    
    Promise.all([
      createMario(),
      loadLevel('1-1'),
      msg.createTxt1(),
      msg.createTxt2(),
      msg.createTxt3(),
    ])
    .then(([mario,level,text1,text2,text3])=>{
      const camera = new Camera();
      window.camera=camera;
   
      mario.pos.set(50,90);
      text1.pos.set(50,90);
      text2.pos.set(50,90);
      text3.pos.set(500,80);
   
      level.entities.add(mario);
      level.entities.add(text1);
      level.entities.add(text2);
      level.entities.add(text3);

      const input=setupKeyboard(mario);
      input.listenTo(window);
   
      const timer= new Timer(1/60);
   
      timer.update =function update(deltaTime){
      level.comp.draw(context,camera);
      level.update(deltaTime);
      if (mario.pos.x>100){
        camera.pos.x=mario.pos.x-100
      }
    }
      timer.start();
    });
  },[])
  
  return(
    <>
    <canvas className="canvas" ref={canvasRef} {...props}/>
    <Navbar/>
    <Router>
      <Switch>
        <Route exact path="./portfolio">
          <Portfolio/>
        </Route>
      </Switch>
    </Router>
    </>
  )
}

export default Canvas
